// Fork of "Blue noise vs Bayer dithering" by Suslik. https://shadertoy.com/view/wl3XWs
// 2024-01-10 18:22:04

float RaySphereIntersection(vec3 rayStart, vec3 rayDir, vec3 sphereCenter, float sphereRadius)
{
	float scale = -1.0;

	float a = dot(rayDir, rayDir);
	vec3 delta = rayStart - sphereCenter;
	float b = 2.0 * dot (delta, rayDir);
	float c = dot(sphereCenter, sphereCenter) + dot(rayStart, rayStart) - 2.0 * dot(rayStart, sphereCenter) - sphereRadius * sphereRadius;
	float disc = b * b - 4.0 * a * c;
	if (disc > 0.0)
	{
		float sqrtDisc = sqrt(disc);

		scale = (-b - sqrtDisc) / (2.0 * a);
	}
	return scale;
}

float RaytraceScene(vec3 rayOrigin, vec3 rayDir, vec3 sphereCenters[3], vec3 lightPos)
{
    for(int i = 0; i < 3; i++)
    {
        float hitScale = RaySphereIntersection(rayOrigin, rayDir, sphereCenters[i], iResolution.x * 0.09);
        if(hitScale < 0.0)
            continue;
        vec3 hitPoint = rayOrigin + rayDir * hitScale;
        vec3 normal = normalize(hitPoint - sphereCenters[i]);

        float diffuse = max(0.0, dot(normal, -normalize(hitPoint - lightPos)));
        return diffuse * 0.8 + 0.2;
    }
    return 0.1;
}

float GetBayerDither(float grayscale, ivec2 pixelCoord)
{    
    //use this for 2x2 bayer test
    /*int bayerMatrix4[4] = int[4](0, 2, 3, 1);
    int pixelIndex4 = (pixelCoord.x % 2) + (pixelCoord.y % 2) * 2;
    return grayscale > (float(bayerMatrix4[pixelIndex4]) + 0.5) / 4.0 ? 1.0 : 0.0;*/

    //use this for 8x8 bayer test
    /*vec2 uv = (vec2(pixelCoord) + vec2(0.5)) / 8.0; //8 is the texture size
    return textureLod(iChannel0, fract(uv), 0.0).x < grayscale ? 1.0 : 0.0; //8x8 creates more artifacts on moving objects than 4x4*/

    //this is the default 4x4 bayer
    int bayerMatrix16[16] = int[16](0, 8, 2, 10, 12, 4, 14, 6, 3, 11, 1, 9, 15, 7, 13, 5);
    int pixelIndex16 = (pixelCoord.x % 4) + (pixelCoord.y % 4) * 4;
    return grayscale > (float(bayerMatrix16[pixelIndex16]) + 0.5) / 16.0 ? 1.0 : 0.0;
}

float GetBlueNoiseDither(float grayscale, ivec2 pixelCoord)
{
    vec2 uv = (vec2(pixelCoord) + vec2(0.5)) / 1024.0; //1024 is the texture size
    float blueNoiseValue = textureLod(iChannel1, fract(uv), 0.0).x;
    //blueNoiseValue = sin(blueNoiseValue * 2.0 * 3.141592 + iTime) * 0.5 + 0.5;
    return blueNoiseValue < grayscale ? 1.0 : 0.0;
}

float GetRandomNoiseDither(float grayscale, ivec2 pixelCoord)
{
    vec2 uv = (vec2(pixelCoord) + vec2(0.5)) / 256.0; //1024 is the texture size
    float blueNoiseValue = textureLod(iChannel2, fract(uv), 0.0).x;
    //blueNoiseValue = sin(blueNoiseValue * 2.0 * 3.141592 + iTime) * 0.5 + 0.5;
    return blueNoiseValue < grayscale ? 1.0 : 0.0;
}

float GetPixelColor(vec2 pixelCoord)
{
    float xPos = mix(iResolution.x * 0.2, iResolution.x * 0.8, sin(iTime) * 0.5 + 0.5);
    float yPos = sin(iTime * 3.0) * 0.05 * iResolution.y * 0.0;
    vec3 sphereCenters[3];
    sphereCenters[0] = vec3(xPos, iResolution.y * 0.1666 + yPos, 0.0);
    sphereCenters[1] = vec3(xPos, iResolution.y * 0.5000 + yPos, 0.0);
    sphereCenters[2] = vec3(xPos, iResolution.y * 0.8333 + yPos, 0.0);

    vec3 lightPos = vec3(iResolution.x * (1.5), iResolution.y * 0.5, -1.5 * iResolution.x);

    vec3 rayOrigin = vec3(iResolution.x * 0.5, iResolution.y * 0.5, -iResolution.x);
    vec3 rayDst = vec3(pixelCoord.xy, 0.0);
    vec3 rayDir = normalize(rayDst - rayOrigin);
    vec3 normals[2];

    float grayscale = RaytraceScene(rayOrigin, rayDir, sphereCenters, lightPos);
    float ditherColor = 0.0;
    ivec2 shift = ivec2(0*iFrame, 1*iFrame);
    if(pixelCoord.y > iResolution.y * 0.6666)
    {
        ditherColor = GetBayerDither(grayscale, ivec2(pixelCoord) + shift);
    }else if(pixelCoord.y > iResolution.y * 0.3333)
    {
        ditherColor = GetBlueNoiseDither(grayscale, ivec2(pixelCoord) + shift);
    } else {
        ditherColor = GetRandomNoiseDither(grayscale, ivec2(pixelCoord) + shift);
    }
    //ditherColor = grayscale; //use for test without dithering
    return ditherColor;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    float linearColor = 0.0;
    if(cos(iTime * 0.5) * 0.5 + -5.5 < fragCoord.x / iResolution.x)
    {
        linearColor = GetPixelColor(fragCoord.xy);
    }else
    {
        ivec2 offset;
        float sumColor = 0.0f;
        float sumWeight = 0.0f;
        for(int x = -2; x < 2; x++)
        {
            for(int y = -2; y < 2; y++)
            {
                sumColor += GetPixelColor(fragCoord.xy + vec2(x, y));
                sumWeight += 1.0;
            }
        }
        linearColor = sumColor / sumWeight;
    }
    fragColor = vec4(pow(vec3(linearColor), vec3(1.0 / 2.2)), 1.0); //linear->srgb manual conversion as shadertoy does not support actual srgb
}
