vec2 normalizeStep(vec2 x) {
    return x*(1.5-.5*dot(x,x));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2./iResolution.y+vec2(-iResolution.x/iResolution.y,-1.);
    uv *= 1.9;
    for(int i=0;i<5;i++) {
      uv=normalizeStep(uv);
    }

    // Output to screen
    fragColor = vec4(.5*(uv+1.),0.,1.0);
}
