float j(float x){

return cos(x);
}



void mainImage( out vec4 color, in vec2 fragCoord )
{

    vec4 firstColor = vec4(1.0,0.0,0.0,1.0); 
    vec4 middleColor = vec4(0.0,1.0,0.0,1.0); 
    vec4 endColor = vec4(0.0,0.0,1.0,1.0); 

    vec2 xy = fragCoord.xy / iResolution.xy;
    float h = 0.5; 


    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;

    float x=uv.x;
    float y=uv.y;

    float s = 0.5+0.05*float(iFrame)/120.;
    float l=1.;
    float f=10.;
    float count=0.;

    color=vec4(1.);
    float x2=0.;
    float y2=0.;
    vec2 curr=vec2(0.);
    float holder=0.;

    float b=-0.;
    for(float r=0.25;r>=0.25*pow(0.85,20.);r*=0.85){
    count=count+1.;
    for(float a=-ceil(0.85/r);a<=ceil(0.85/r);a++){
    if(abs(-b+x+y+(a-mod(count*s/(r*1.5),l))*r*1.5-0.1*j(f*(a-mod(count*s/(r*1.5),l))*r*1.5))+abs(-b+y-x-(a-mod(count*s/(r*1.5),l))*r*1.5-0.1*j(f*(a-mod(count*s/(r*1.5),l))*r*1.5))<r){

    float center_x=(-a+mod(count*s/(r*1.5),l))*r*1.5+0.5;
    vec4 col = vec4(1.)-mix(mix(firstColor, middleColor, center_x/h), mix(middleColor, endColor, (center_x - h)/(1.0 - h)), step(h, center_x));
    x2=0.5-(a-mod(count*s/(r*1.5),l))*r*1.5;
    y2=0.5+iResolution.x/iResolution.y*(0.1*j(f*(a-mod(count*s/(r*1.5),l))*r*1.5)+b);
    curr=vec2(x2,y2);
    holder=length(xy-curr);
    holder=pow(pow(xy.x-curr.x,2.)+pow(xy.y*(iResolution.y/iResolution.x)-curr.y*(iResolution.y/iResolution.x),2.),0.5);
    color-=2.*pow(2.,-50.*holder)*r*col;

    }
    }
    }



}
