#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float GetDist(vec3 p){
    vec4 s = vec4(0, 1, 6, 1);
    //float displacement = sin(5.0 * p.x) * sin(5.0 * p.y) * sin(5.0 * p.z) * 0.25;

    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = p.y;

    float d = min(sphereDist, planeDist);
    return d;

    //return sphereDist;
    //return sphereDist + displacement;
}

float RayMarch(vec3 ro, vec3 rd){
    float d0 = 0.;

    for(int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro + rd * d0;
        float ds = GetDist(p);
        d0 += ds;

        if(d0 > MAX_DIST || ds < SURF_DIST){
            break;
        }

    }

    return d0;
}

vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
    GetDist(p - e.xyy),
    GetDist(p - e.yxy),
    GetDist(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p){
    vec3 lightPosition = vec3(0, 5, 6);
    lightPosition.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    lightPosition.y += sin(iTime)*3.;
    vec3 l = normalize(lightPosition - p);
    vec3 n = GetNormal(p);

    float diff = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n *  SURF_DIST * 4., l);
    if(d < length(lightPosition - p)){
        diff *= .1;
    }
    return diff;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) /iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);

    fragColor = vec4(col,1.0);
}
