const float PI = acos(-1.),
			DEG = PI/180.,
			TAU = 2.*PI,
			PHI = .5*(1.+sqrt(5.));
float T, lTHICK = PHI, SCALE = 4., AR;
//========================
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){
    return a + b*cos( TAU*(c*t+d) );
}
//========================
mat2 rot(float a){
  float c=cos(a), s=sin(a);
  return mat2(c,-s,s,c);
}
//========================
vec3 shadePoint(vec3 col, vec3 col1, float fValue, float thick) {
    col = mix( col, col1, smoothstep( thick, .0, abs(fValue)/fwidth(fValue) ) );
    return col;
}
//========================
vec3 p2(vec2 uv, vec3 COL, vec3 COL1, float roCoef){
  float ro, t, a, n, m, pp;
  for (float rounds = 6., i=0.;i<rounds;i++) {
    ro = length(uv), t = atan(uv.y, uv.x),
    a=(i+.5+sin(T))*.5, m=1., n=2.,
    pp=a*cos(m*t)*cos(n*t)-ro*roCoef; // TORPEDO CURVE // https://mathcurve.com/courbes2d.gb/torpille/torpille.shtml;
    COL = shadePoint(COL, COL1, pp, lTHICK);
  }
  return COL;  
}
//========================
void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
  AR = iResolution.x / iResolution.y;
  uv *= SCALE;
  T = 0.125*iTime;
  vec3 COL = vec3(0., 0., .15), COL1;
  float b = SCALE*.55, a = b*1.5*AR, xt = cos(3.*T), yt = sin(2.*T),
    	wingAngle = (.5+.5*sin(T*5.))*90.*DEG;
  vec2 m = vec2(xt*a, yt*b);
  float ba = (.25)*PI;
  COL1= pal( uv.x*uv.y, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,0.5),vec3(0.8,0.90,0.30) ); COL = p2((uv - m)*rot(ba), COL, COL1, 1.5);
  COL1= pal( uv.x+uv.y, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(1.0,1.0,1.0),vec3(0.0,0.25,0.25) ); COL = p2((uv - m)*rot(ba+wingAngle), COL, COL1, 1.1);
  COL1= pal( uv.x-uv.y, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) ); COL = p2((uv - m)*rot(ba-wingAngle), COL, COL1, 1.2);
  fragColor = vec4(COL, 1.);
}
//========================
