float rand(vec2 co, float seed)
{
    vec2 p = co + seed;
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 bgColor = vec3(0.98, 1, 0.992);
vec3[] fgColors = vec3[4](
    vec3(0.235, 0.569, 0.902),
    vec3(0.624, 0.827, 0.337),
    vec3(0.204, 0.18, 0.216),
    vec3(0.98, 0.51, 0.298)
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv.x -= iResolution.x / (2.0 * iResolution.y); 

    float shapeSeed = 45436.2342 + floor(iTime);
    float colorSeed = 42352.6335 + floor(iTime + 0.5);

    uv *= 5.0;

    vec2 idx = floor(uv);
    float r = floor(rand(idx, shapeSeed) * 3.0);

    if(r == 1.0) {
        uv.x += 0.5;
    } else if(r == 2.0) {
        uv.y += 0.5;
    }

    uv = fract(uv);

    vec2 pos = uv - 0.5;
    vec3 fgColor = fgColors[int(rand(idx/5.0, colorSeed) * 4.0)];


    fragColor = vec4(mix(fgColor, bgColor, smoothstep(0.24, 0.26, dot(pos, pos))), 1.000);


}
