void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float n_factor = .5;
    vec2 o_res = iResolution.xy * n_factor;
    vec2 o_fc = fragCoord.xy * n_factor;
    float n_scl_min = min(o_res.x, o_res.y);
    vec2 o_trn = (o_fc.xy-(o_res.xy*.5))/n_scl_min;

    float n_tau = radians(360.);
    float n_ang_nor = fract(atan(o_trn.x, o_trn.y)/n_tau);
    float n_d_cntr = length(o_trn);
    float n_d_cntr_snapped = floor(n_d_cntr*n_scl_min*2.)/n_scl_min*2.;
    float n_pixels = n_scl_min;
    float n_pixels_per_circumfence = n_scl_min*n_d_cntr_snapped;
    float n_pixel = floor(n_ang_nor * n_pixels_per_circumfence);
    float n_ang_nor_snapped = n_pixel/n_pixels_per_circumfence;
    float n_ang_nor_snapped2 = 1.-(n_pixel/n_pixels_per_circumfence);
    float n = (mod(n_pixel, 2.) == 0.) ?  n_ang_nor_snapped : n_ang_nor_snapped2;
    fragColor = vec4(n);

}
