void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14159265359;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    ivec2 fragxy = ivec2(fragCoord.xy);

    vec3 quantize_amount = vec3(2.0,0.0,0.0);

    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = 0.5 + 0.5*cos(uv.xyx*10.0+vec3(1,2,4));
    vec3 delta = quantize_delta(col, quantize_amount, vec3(1.0));
    vec3 up = quantize_up(col, quantize_amount);
    vec3 down = quantize_down(col, quantize_amount);

    float example_size = 1.0/14.0;
    if(uv.y>0.0 && uv.y < example_size){
        fragColor = vec4(col,1.0);
    } else if(uv.y> example_size && uv.y < example_size * 2.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_bayer2_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bayer2_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bayer2_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 2.0 && uv.y < example_size * 3.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_bayer4_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bayer4_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bayer4_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 3.0 && uv.y < example_size * 4.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_bayer8_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bayer8_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bayer8_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 4.0 && uv.y < example_size * 5.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_bayer16_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bayer16_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bayer16_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 5.0 && uv.y < example_size * 6.0){
        vec3 res = vec3(0.0);
        // DIMINISHING RETURNS!
        res.r = mix(down.r, up.r, get_bayer32_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bayer32_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bayer32_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 6.0 && uv.y < example_size * 7.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_bluenoise_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_bluenoise_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_bluenoise_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 7.0 && uv.y < example_size * 8.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_scanline_mix_amount(delta.r, fragxy, 0.0*PI));
        res.g = mix(down.g, up.g, get_scanline_mix_amount(delta.g, fragxy, 0.5*PI));
        res.b = mix(down.b, up.b, get_scanline_mix_amount(delta.b, fragxy, 0.26*PI));
        fragColor = vec4(res,1.0);
    } else if(uv.y> example_size * 8.0 && uv.y < example_size * 9.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_valvescreendither_mix_amount(delta.r, fragxy, vec3(1.0, 0.0, 0.0)));
        res.g = mix(down.g, up.g, get_valvescreendither_mix_amount(delta.g, fragxy, vec3(0.0, 1.0, 0.0)));
        res.b = mix(down.b, up.b, get_valvescreendither_mix_amount(delta.b, fragxy, vec3(0.0, 0.0, 1.0)));
        fragColor = vec4(res,1.0); 
    } else if(uv.y> example_size * 9.0 && uv.y < example_size * 10.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_random_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_random_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_random_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0); 
    } else if(uv.y> example_size * 10.0 && uv.y < example_size * 11.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_triangular_random_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_triangular_random_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_triangular_random_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0); 
    } else if(uv.y> example_size * 11.0 && uv.y < example_size * 12.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_violet_noise_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_violet_noise_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_violet_noise_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0); 
    } else if(uv.y> example_size * 12.0 && uv.y < example_size * 13.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_JJ_IGN_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_JJ_IGN_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_JJ_IGN_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0); 
    } else if(uv.y> example_size * 13.0 && uv.y < example_size * 14.0){
        vec3 res = vec3(0.0);
        res.r = mix(down.r, up.r, get_GRI_IGN_mix_amount(delta.r, fragxy));
        res.g = mix(down.g, up.g, get_GRI_IGN_mix_amount(delta.g, fragxy));
        res.b = mix(down.b, up.b, get_GRI_IGN_mix_amount(delta.b, fragxy));
        fragColor = vec4(res,1.0); 
    }


}

const float NOISE_SMALL_NUMBER = 0.00001;
const uint NOISE_HILBERT_LEVEL = 7U;
const uint NOISE_HILBERT_WIDTH = (1U << NOISE_HILBERT_LEVEL);

vec3 quantize_down(vec3 n, vec3 size){
    return floor(n * size) / (size);
}
vec3 quantize_up(vec3 n, vec3 size){
    return ceil(n * size) / (size);
}
vec3 quantize_delta(vec3 n, vec3 size, vec3 edge_offset){
    //vec3 up = quantize_up(n, size)-n;
    vec3 down = n-quantize_down(n, size);
    return 1.0-(down/(1.0/size)*edge_offset);
}

float quantize_down(float n, float size){
    return floor(n * size) / (size);
}
float quantize_up(float n, float size){
    return ceil(n * size) / (size);
}


