int convert_to_bayer_dither_value(float linear, int target_matrix_size){
    return int(float(target_matrix_size) * linear);
}

int get_bayer2_dither_value(ivec2 fragcoords){ return BAYER2_MATRIX[((fragcoords.y*2)+fragcoords.x)%BAYER2_MATRIX_SIZE]; }
