float get_bayer2_mix_amount(float amount, ivec2 fragcoords){
    if( get_bayer2_dither_value(fragcoords) > convert_to_bayer_dither_value(amount - NOISE_SMALL_NUMBER, BAYER2_MATRIX_SIZE)) return 1.0;
    return 0.0;
}

