// Inspired by paniq
uint get_bluenoise_hilbert_index(uvec2 fragcoords) {
	uint i = 0U;
	for( uint cl = NOISE_HILBERT_WIDTH/2U; cl > 0U; cl /= 2U ) {
		uvec2 r = uvec2(greaterThan((fragcoords & uvec2(cl)), uvec2(0U)));
		i += cl * cl * ( (3U * r.x) ^ r.y);
		if( r.y == 0U ) {
			if( r.x == 1U )  fragcoords = uvec2(NOISE_HILBERT_WIDTH - 1U) - fragcoords;
			fragcoords.xy = fragcoords.yx;
		}
	}
	return i;
}

float get_bluenoise_dither_value(ivec2 fragcoords){ 
	return fract(1.11803398874989484820*float(get_bluenoise_hilbert_index(uvec2(fragcoords))));
}

float get_bluenoise_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_bluenoise_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}


float get_scanline_dither_value(ivec2 fragcoords, float rotation){
    vec2 rotated_fragcoords = vec2(
        cos(rotation) * float(fragcoords.x) + sin(rotation) * float(fragcoords.y),
        cos(rotation) * float(fragcoords.y) - sin(rotation) * float(fragcoords.x)
    );

    return floor(mod(float(rotated_fragcoords.y),2.0))*0.5;
}

float get_scanline_mix_amount(float amount, ivec2 fragcoords, float rotation){
    return 1.0-step(get_scanline_dither_value(fragcoords, rotation)+0.25+NOISE_SMALL_NUMBER, amount);
}

// from http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
float get_valvescreendither_dither_value( ivec2 fragcoords, vec3 element){
	// Iestyn's RGB dither (7 asm instructions) from Portal 2 X360, slightly modified for VR
	//vec3 vDither = vec3( dot( vec2( 171.0, 231.0 ), vScreenPos.xy + iTime ) );
    vec3 vDither = vec3( dot( vec2( 171.0, 231.0 ), vec2(fragcoords.xy) ) );
    vDither.rgb = fract( vDither.rgb / vec3( 103.0, 71.0, 97.0 ) );

    return (vDither.r*element.r)+(vDither.g*element.g)+(vDither.b*element.b);
}

float get_valvescreendither_mix_amount(float amount, ivec2 fragcoords, vec3 element){
    return 1.0-step(get_valvescreendither_dither_value(fragcoords, element)+NOISE_SMALL_NUMBER, amount);
}


//note: from https://www.shadertoy.com/view/4djSRW
// This set suits the coords of of 0-1.0 ranges..
float get_dither_hash11_value(float p){
	vec3 p3  = fract(vec3(p) * vec3(443.8975,397.2973, 491.1871));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
float get_dither_hash12_value(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.8975,397.2973, 491.1871));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float get_triangular_random_dither_value(ivec2 fragcoords){
    return get_dither_hash12_value( vec2(fragcoords) ) + get_dither_hash12_value(vec2(fragcoords) + 0.59374) - 0.5;
}

float get_triangular_random_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_triangular_random_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}

float get_random_dither_value(ivec2 fragcoords){
    return get_dither_hash12_value( vec2(fragcoords));
}

float get_random_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_random_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}


// Thanks to Spalmer - see http://shadertoy.com/view/Wtfczs
float get_violet_noise_dither_value(ivec2 fragcoords){
    vec2 p = vec2(fragcoords) * 2.0;
    return .5 
        + ((get_dither_hash12_value(p + vec2(1,0)) - get_dither_hash12_value(p - vec2(1,0))) * 0.5)
        + ((get_dither_hash12_value(p + vec2(0,1)) - get_dither_hash12_value(p - vec2(0,1))) * 0.5);
}

float get_violet_noise_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_violet_noise_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}

// Jorge Jimenez interleaved gradient noise;
// see https://www.shadertoy.com/view/XfBSRy
float get_JJ_IGN_dither_value(ivec2 fragcoords)
{  
    vec3 magic = vec3( 0.06711056f, 0.00583715f, 52.9829189f );   
    return fract(magic.z * fract(dot(vec2(fragcoords), magic.xy)));   
}

float get_JJ_IGN_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_JJ_IGN_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}

