// Golden Ratio Integer
float get_GRI_IGN_dither_value(ivec2 fragcoords)
{  
    uvec2 umagic = uvec2(3242174889u, 2447445413u); //1/phi, 1/phiÂ² * 2^32, replaces frac() with a static precision method     
    return float(uint(fragcoords.x) * umagic.x + uint(fragcoords.y) * umagic.y) * exp2(-32.0);   
}

float get_GRI_IGN_mix_amount(float amount, ivec2 fragcoords){
    return 1.0-step(get_GRI_IGN_dither_value(fragcoords)+NOISE_SMALL_NUMBER, amount);
}
