void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float col,x,y,z,hips, heels=230., pelvis=80.;

hips = sin(iTime*5.);
x = (iResolution.x/2.-fragCoord.x)/iResolution.x;
y = fragCoord.y/iResolution.y;
z = abs(iResolution.y-fragCoord.y)/(heels-(sin(hips*x)+cos(hips*y))*pelvis);



    // ddddance ffffloor
    col=( 
            sin(x*50./z+iTime)+
            cos(y*50./z+iTime*13.)
         ) * (z/8.);

    // Output to screen
    fragColor = vec4(col-0.2,col,col-0.4,1.0);

}
