void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

float n=0.01;
float j=0.375;
float f1(float x, float s, float b, float c,float a){

    float k=3.;
    if(abs(x)<2.){
        return pow(x-b,2.)+pow(s+mod(a+cos(x),k)-c,2.);
    }else{
        return 10.;
    }
}

float g1(float s, float b,float c,float a){
    float holder=1000.;
    for(float i=b-j;i<b+j;i+=n){
        float holder2=f1(i,s,b,c,a);
        if(holder2<holder){
            holder=holder2;
        }
     }
     return holder;
}



float g2(float s, float b,float c,float a){
    float holder=1000.;
    for(float i=b-j;i<b+j;i+=n){
        float holder2=f1(i,s,b,-c,a);
        if(holder2<holder){
            holder=holder2;
        }
     }
     return holder;
}

float g3(float s, float b,float c,float a){
    float holder=1000.;
    for(float i=c-j;i<c+j;i+=n){
        float holder2=f1(i,s,c,b,a);
        if(holder2<holder){
            holder=holder2;
        }
     }
     return holder;
}

float g4(float s, float b,float c,float a){
    float holder=1000.;
    for(float i=c-j;i<c+j;i+=n){
        float holder2=f1(i,s,c,-b,a);
        if(holder2<holder){
            holder=holder2;
        }
     }
     return holder;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    float a = floor(float(iFrame));
    float skip=3.;
    if(mod(a,skip)<=0.){
    float a=1.+float(iFrame)/(skip*40.);
    float g=25.;
    vec2 uv = g*fragCoord/iResolution.x;
    uv.x=uv.x-g/2.;
    uv.y=uv.y-iResolution.y/iResolution.x*g/2.;

    float min1=-0.;
    float max=2.;
    float step=1.;
    float d1=100.;
    for(float i=min1;i<=max;i+=step){
        d1=min(d1,g1(i,uv.x,uv.y,a));
    }
    float d2=100.;
    for(float i=min1;i<=max;i+=step){
        d2=min(d2,g2(i,uv.x,uv.y,a));
    }
    float d3=100.;
    for(float i=min1;i<=max;i+=step){
        d3=min(d3,g3(i,uv.x,uv.y,a));
    }
    float d4=100.;
    for(float i=min1;i<=max;i+=step){
        d4=min(d4,g4(i,uv.x,uv.y,a));
    }

    float d=min(min(d1,d2),min(d3,d4));
    vec3 col = pow(2.,-25.*d)*vec3(1.);

    fragColor = vec4(col,1.0);

    }else{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv); 
    }



}
