#define AR (16./9.)

vec2 nuv;

vec2 nx(vec2 v) {
    return vec2(v.x*AR, v.y);
}

vec2 rot(vec2 c, vec2 p, float f) {
    mat2 rm = mat2(cos(f), -sin(f), sin(f), cos(f));
    return rm*(p - c) + c;
}

vec2 get_norm_point(vec2 p1, vec2 p2) {
    vec2 pp = p2 - p1;
    vec2 upp = normalize(pp);
    vec2 uvp = nuv - p1;
    float len_po = dot(upp, uvp);
    return p1 + upp*len_po;
}

bool is_between_points(vec2 p1, vec2 p2, vec2 o) {
    float len_pp = length(p2 - p1);
    return (length(o - p1) < len_pp && length(p2 - o) < len_pp);
}

void mainImage( out vec4 Col, in vec2 Crd )
{
    vec2 uv = Crd/iResolution.xy;
    nuv = nx(uv);

    float delta = .001;

    vec4 p_col = vec4(1., 0., 1., 1.);
    float r = .03;
    float p_dif = .1;

    vec2 p1 = vec2(.5);
    vec2 p2 = vec2(p1 + p_dif);
    vec2 p3 = vec2(p1.x + p_dif, p1.y - p_dif);

    p1 = nx(p1);
    p2 = nx(p2);
    p3 = nx(p3);

    p2 = rot(p1, p2, iTime/2.);
    p3 = rot(p2, p3, iTime/2.);

    vec4 l_col = vec4(0., 1., 0., 1.);
    float thick = .012;
    vec2 o = get_norm_point(p2, p3);

    Col = vec4(vec3(uv.x), 1.);
    Col = mix(p_col, Col, smoothstep(r - delta, r + delta, length(nuv - p1)));
    if (is_between_points(p2, p3, o))
        Col = mix(l_col, Col, smoothstep(thick/2. - delta, thick/2. + delta, length(nuv - o)));
    Col = mix(p_col, Col, smoothstep(r - delta, r + delta, length(nuv - p2)));
    Col = mix(p_col, Col, smoothstep(r - delta, r + delta, length(nuv - p3)));

}
