float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float d =sdCircle(uv, .1);
    float w = 0.1;

    // s意味ないかも
    float s = .3;
    s += abs(sin(iTime));

    d = step(d, s) * step(s - w, d);

    vec3 col = vec3(d, d, d);

    fragColor = vec4(col,1.0);
}
