void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 white = vec3(1, 1, 1);
    vec2 circCenter = vec2(0, 0);
    float circRadius = 0.2;

    // Center the coordinates
    vec2 uv = fragCoord - (iResolution.xy / 2.0);
    // Normalized pixel coordinates (from 0 to 1) with respect to the y axis
    uv = uv / iResolution.y;

    float distCenter = sqrt(pow(uv.x - circCenter.x, 2.0) + pow(uv.y - circCenter.y, 2.0));

    float isInCircle = float(distCenter <= circRadius);

    vec3 col = white * isInCircle;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
