#define PI 3.1415926535897932384626433832795
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.;

    vec2 o = vec2(.5);

    float T = 1.;
    float L = 0.1;
    float a = (sin(iTime)*2. + 3.)*sin(2.*PI * (iTime /T - distance(o, uv) / L)); 
    float b = sin(2.*PI * (iTime /T - distance(o, uv) / L));
    float c = sin(2.*PI * (iTime /T - distance(uv, vec2(1.8, 0.5)) / L));

    // Output to screen
    fragColor = vec4(vec3(b + c) / 2. + .5,1.0);
}
