uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;

#define time iTime
#define resolution iResolution.xy






void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 p = 2.0*( gl_FragCoord.xy / resolution.xy )-1.0;
float t = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(p.xy) + .07)) * 2.2;
float si = sin(t);
float co = cos(t);
mat2 ma = mat2(co, si, -si, co);
  p*=ma;
p.xy*=mat2(cos(iTime*1.),sin(iTime*1.),-sin(iTime*1.), cos(iTime*1.));
p.x *= resolution.x/resolution.y;
vec3 col = vec3(0);


for (int i = 0; i < 100; i++) {
float lt = 0.01;
float a = float(i);
vec2 b = vec2(1.5*sin(time+a),0.8*sin(time*0.77+a));

if (abs(p.x) < b.x && abs(p.y) < b.y) {

if (abs(p.x) > b.x-lt || abs(p.y) > b.y-lt )
col = vec3(1);
}
}
fragColor = vec4(col, 1.0);
}
