#define baseScale 10.
#define speed 15.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord.xy / iResolution.y;
  uv *= baseScale;

  vec2 cell = floor(uv);

  vec3 cellCol = vec3(0.0);
  cellCol += mod(cell.x+cell.y, 2.) * 0.3; // checkerboard

  float cellCountByRow = iResolution.x/iResolution.y*baseScale;
  float squareCol = mod(iTime * speed, cellCountByRow) - 1.; // increments by 1 every seconds (* speed), restart at cellCountByRow.
  float squareRow = mod(iTime * speed/cellCountByRow, baseScale) - 1.; // increments by 1 every 10 seconds (* speed), restart at baseScale.

  cellCol += step(squareCol, cell.x) * step(cell.x, squareCol+1.) * step(squareRow, cell.y) * step(cell.y, squareRow+1.);

  fragColor = vec4(cellCol, 1.0);
}
