vec3 White = vec3(241, 241, 241)/255.;
vec3 Red = vec3(218, 14, 15)/255.;

vec3 Flag(vec2 uv)
{   
    uv = uv - vec2(.5, .5);

    if(length(uv) < .15)
    {
        return Red;
    } else {
        return White;    
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    float cost=cos(iTime+uv.y*4.0)*1.0;

    uv.y += sin(uv.x * 5. + iTime * -5.) *.05;
    uv.x += cos(uv.y * 10. + iTime * -5.) * 0.02;

    vec3 col = Flag(uv);
    fragColor = vec4(col, 1.);

    if(uv.y<(.01*cost+.01)){

        fragColor = vec4(0);
    }
    if(uv.y>(0.01*cost+1.)){
        fragColor = vec4(0);
    }
    if(uv.x<0.01){
     fragColor = vec4(0);
    }
    if(uv.x>1.){
     fragColor = vec4(0);
    }
}
