float sdArcOutline(vec2 pos, float rad, float thickness, float startAngle, float endAngle) {
    float angle = atan(pos.y, pos.x);
    float len = length(pos);
    float arcAngleDist = max(0.0, abs(angle - (startAngle + endAngle) / 2.0) - (endAngle - startAngle) / 2.0);
    float distToCircle = abs(len - rad);
    float outlineDist = max(arcAngleDist * rad, distToCircle) - thickness;
    return outlineDist;
}

mat2 rotate(float angle) {
  return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

  vec2 arcPos = rotate(-radians(mod(iTime * 300., 360.))) * uv;
  float arc = sdArcOutline(arcPos, 0.3, 0.03, radians(0.), radians((1.+cos(iTime * 3.)) * 45.));

  vec3 col  = (arc > 0.01) ? vec3(0.059,0.09,0.157) : vec3(0.886,0.91,0.941);

  fragColor = vec4(col, 1.0);
}
