void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float STAR_SIZE = 0.05;
    const float TRAIL_OPACITY = 0.8;

    vec2 uv = fragCoord/iResolution.xy;

    vec4 buffer = texture(iChannel0, uv);

    vec4 mouse = iMouse/vec4(iResolution.xy, 1.0, 1.0);
    float star = 1.0 - distance(uv,vec2(mouse.xy)) * (1.0 / STAR_SIZE);

    star = clamp(star, 0.0, 1.0) * clamp(mouse.z, 0.0, 1.0);

    fragColor = max(vec4(star, star, star, 1.0), buffer * TRAIL_OPACITY);
}
