mat2 rotate(float amount) {
    return mat2(cos(amount), -sin(amount), sin(amount), cos(amount));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    float rotation = iTime/2.0;

    uv = rotate(rotation) * uv;

    vec3 color = texture(iChannel0, uv).xyz;
    color += 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor.rgb = color;
}
