vec3 palette(float t, vec3 b,  vec3 s, vec3 d, vec3 m) {
  return b + s * cos(6.2831853071795864 * (t * m + d));
}

#define ITERS 80

float julia(vec2 z) {
  for (int i = 0; i < ITERS; i++) {
    if (dot(z, z) > 4.0) {
      return float(i) / float(ITERS);
    }
    z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y)
      + vec2(.12283 + cos(iTime) * .1, -.62093);
  }
  return sin(length(z)) * .5 + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  // uvx2
  vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x;

  // julia set
  float val = julia(uv * 4.5);
  vec3 col = palette(val * .6 + 1.2, vec3(0.50,0.50,0.50), vec3(0.50,0.50,0.50), vec3(0.47,0.57,0.67), vec3(0.90,0.90,0.90));

  // background
  col = max(col, texture(iChannel0, uv).rgb);

  // output to screen
  fragColor = vec4(col, 1.0);
}
