void mainImage( out vec4 O, vec2 u )
{
   vec2 R = iResolution.xy,
        U = ( u - .5*R ) / R;            // normalized coordinates

   float s = 1.;                         // zoom in/out with your head ;-)
// float s = 2. + cos(1.5*iTime);        // strangely, effect disappears with automatic zoom

   O = texture(iChannel0, U / s / 2.  ); // zoomed white noise

   if (U.x > 0.)                         // right: moving stripes
       O *= step( sin(30.*U.y + 3.*iTime ), 0.);
}

//#define hash31(p)  fract(sin(dot(p, vec3(12.9898, 78.233, 311.7))) * 43758.5453)

float hash31(vec3 p3){
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 O, vec2 U )
{
   O = vec4( hash31(vec3(U,iTime) ) );
}
