void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //This value modifies the size of the blur

    float blur = 10.0;

    vec3 color = vec3(0.0);

    for (float i = -blur; i <= blur; i++) {
        for (float j = -blur; j <= blur; j++) {
            float distance = sqrt(i * i + j * j);
            float weight = smoothstep(blur, blur - 1.0, distance);
            color += texture(iChannel0, (fragCoord + vec2(i, j)) / iResolution.xy).rgb * weight;
        }
    }

    color /= (blur)*(blur+blur+blur);
    fragColor = vec4(color, 1.0);
}
