#define N normalize//
#define Z mat2(z,-z.y,z)

float d( vec2 z )
{         
    vec2 d,i;     
    for( ; i.x++ < 50.+iTime*5. && dot(z,z) < 1e8 ; )       
        d = 2.* Z * d , d.x++,
        z =     Z * z + vec2(-.7,.3);		

    return length(z) / length(d) * log(length(z));
}

void mainImage( out vec4 O, vec2 v)
{
    vec2 R = iResolution.xy,
         u = 1.55 * ( v+v - R ) / R.x,
         e ; e.x = 1e-3;

    float x = 2.* dot( N(vec3(N( vec2( d(u+e)-d(u-e), d(u+e.yx)-d(u-e.yx) )  ), .2)) , 
                       N(vec3(.1,.05,1)) ),
          c = x* ( 12.*x*x*x + 1.-x ) / 2.5;

    O = vec4( abs(c)*(c*c*2.+.1) + 1. - x*(1.-x)*4. );
}



