// @GregRostami, 393ch  (+2 cause I hate for loops)
#define r(a) mat2(cos(a+vec4(0,33,11,0))) //
#define L length

void mainImage(out vec4 o, vec2 u) {
    vec3 q, p = iResolution;
    u = (u + u - p.xy) / p.y;
    float i, d, b, c, a, T = iTime * .5;

    o *= 0.;
    while(i++ < 1e2)
        p = (.4 + cos(T) * .1) * vec3(u * a, a - .1),

        p.yx *= r(T * .73),
        p.xz *= r(T),

        d = dot(p, p),

        p = cos(p * .28 / d),
        q = vec3(L(p.xy), L(p.yz), L(p.xz)),
        c = min(q.x, min(q.y, q.z)),
        a += d *= min(c - .04, b = 3.6 * L(p) - .6),
        o *= 1. - step(c, b) * q.xyzz,
        o += .02 / exp(4e3 * d - .5);
}


