// scene copied from Nathan https://www.shadertoy.com/view/fdlGWX

const int MAX_MARCHING_STEPS = 255;

const float PRECISION = 1e-4;
const float MAX_DIST = 100.0;

float sdSphere(vec3 p, float r )
{
  return length(p) - r;
}

struct Material {
  vec3 ambientColor; // k_a * i_a
  vec3 diffuseColor; // k_d * i_d
  vec3 specularColor; // k_s * i_s
  float alpha; // shininess
};

struct Surface {
  int id; // id of object
  float sd; // signed distance
  Material mat;
};

Material sphere1() {
  vec3 aCol = 0.5 * vec3(0.7, 0.5, 0);
  vec3 dCol = 0.6 * vec3(0.7, 0.7, 0);
  vec3 sCol = 0.6 * vec3(1, 1, 1);
  float a = 4.;

  return Material(aCol, dCol, sCol, a);
}

Material sphere2() {
  vec3 aCol = 0.4 * vec3(0, 0.5, 0.7);
  vec3 dCol = 0.5 * vec3(0, 0.7, 0.7);
  vec3 sCol = 0.6 * vec3(1, 1, 1);
  float a = 64.;

  return Material(aCol, dCol, sCol, a);
}

Material checkerboard(vec3 p) {
  vec3 aCol = 0.5 + 0.5 * vec3(-1. + mod(floor(p.x) + floor(p.z), 2.0));
  vec3 dCol = vec3(0.3);
  vec3 sCol = vec3(0);
  float a = 1.;

  return Material(aCol, dCol, sCol, a);
}

Surface opUnion(Surface obj1, Surface obj2) {
  if (obj2.sd < obj1.sd) return obj2;
  return obj1;
}

Surface scene(vec3 p) {
//  Surface sFloor = Surface(1, p.y + 1., checkerboard(p));
  Surface sSphere1 = Surface(2, sdSphere(p - vec3(-2, 0, 0), 1.), sphere1());
  Surface sSphere2 = Surface(3, sdSphere(p - vec3(2, 0, 0), 1.), sphere2());

  Surface co = opUnion(sSphere1, sSphere2); // closest object
//  co = opUnion(co, sSphere2);
  return co;
}

Surface rayMarch(vec3 ro, vec3 rd) {
  float depth = 0.0;
  Surface co; // closest object

  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    co = scene(p);
    depth += abs(co.sd);
    if (abs(co.sd) < PRECISION || depth > MAX_DIST) break;
  }

  co.sd = depth;

  return co;
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * scene(p + e.xyy).sd +
      e.yyx * scene(p + e.yyx).sd +
      e.yxy * scene(p + e.yxy).sd +
      e.xxx * scene(p + e.xxx).sd);
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
    vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
    vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
    vec3 cu = normalize(cross(cd, cr)); // camera up

    return mat3(-cr, cu, -cd);
}

vec3 phong(vec3 lightDir, float lightIntensity, vec3 normal, vec3 rd, Surface co) {
  if (co.sd > MAX_DIST) return texture(iChannel0, rd).rgb;

  // ambient
  vec3 ambient = co.mat.ambientColor;

  // diffuse
  float dotLN = clamp(dot(-lightDir, normal), 0., 1.);
  vec3 diffuse = co.mat.diffuseColor * dotLN;

  // specular
  float dotRV = clamp(dot(reflect(lightDir, normal), -rd), 0., 1.);
  vec3 specular = co.mat.specularColor * pow(dotRV, co.mat.alpha);

  vec3 col = lightIntensity * (ambient + diffuse + specular);

  // fog
  //col = mix(col, bgColor, 1.0 - exp(-0.0002 * co.sd * co.sd * co.sd));
  return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  vec3 col = vec3(0);

  vec3 lp = vec3(0); // lookat point (aka camera target)
  vec3 ro = vec3(5. * cos(-iTime), 0., 5. * sin(-iTime));

  vec3 rd = camera(ro, lp) * normalize(vec3(uv, -1)); // ray direction

  Surface co = rayMarch(ro, rd); // closest object

  vec3 p = ro + rd * co.sd; // point on surface found by ray marching
  vec3 normal = calcNormal(p); // surface normal

  // light
  vec3 lightPosition = 4. * vec3(cos(iTime), 1., sin(iTime));
  vec3 lightDirection = normalize(p - lightPosition);
  float lightIntensity = 0.9;

  // phone light model
  col = phong(lightDirection, lightIntensity, normal, rd, co); 

/*  // soft shaddow from iq
  if (co.id == 1 && co.sd < .2 * MAX_DIST) {
    float softShadow = clamp(softShadow(p, -lightDirection, 100.*PRECISION, length(p-lightPosition)), 0.2, 1.0);
    col *= softShadow;
  }
//*/

//*  // reflection;
  if (co.id == 2 && scene(p).sd < 10. * PRECISION) {
    vec3 reflRD = reflect(rd, normal);
    vec3 reflRO = p + 1.1 * PRECISION * normal; // move origin a little bit away
    Surface reflCO = rayMarch(reflRO, reflRD);
    vec3 reflP = reflRO + reflRD * reflCO.sd;
    vec3 reflNormal = calcNormal(reflP);
    vec3 reflCol = phong(lightDirection, lightIntensity, reflNormal, reflRD, reflCO);
    col = mix(col, reflCol, 0.5);
  }
//*/

//*  // refraction
  if (co.id == 3 && scene(p).sd < 10. * PRECISION) {
    const float ETA = 1.69; // glass 1.69, water 1.33ï¼Œ1.0 air
    vec3 refrRD1 = refract(rd, normal, 1./ETA);
    vec3 refrRO1 = p - 2.1 * PRECISION * normal; // move origin into the sphere
    Surface refrCO1 = rayMarch(refrRO1, refrRD1);
    vec3 refrP1 = refrRO1 + refrRD1 * refrCO1.sd;
    vec3 refrN1 = calcNormal(refrP1);

    vec3 refrRD2 = refract(refrRD1, -refrN1, ETA);
    vec3 refrRO2 = refrP1 + 2.1 * PRECISION * refrN1; // move origin outside the sphere
    Surface refrCO2 = rayMarch(refrRO2, refrRD2);
    vec3 refrP2 = refrRO2 + refrRD2 * refrCO2.sd;
    vec3 refrN2 = calcNormal(refrP2);
    vec3 refrCol = phong(lightDirection, lightIntensity, refrN2, refrRD2, refrCO2);
    col = mix(col, refrCol, 0.5);
  }
//*/
  col = pow(col, vec3(1.0/2.2)); // Gamma correction
  fragColor = vec4(col, 1.0); // Output to screen
}

float max3(vec3 rd) {
   return max(max(rd.x, rd.y), rd.z);
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    vec3 rd = abs(rayDir);

    vec3 col = vec3(0);
    if (max3(rd) == rd.x) col = vec3(1, 0, 0);
    if (max3(rd) == rd.y) col = vec3(0, 1, 0);
    if (max3(rd) == rd.z) col = vec3(0, 0, 1);

    fragColor = vec4(col,1.0); // Output cubemap
}
