void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.y/iResolution.x;
    float value;
	vec2 uv = ( fragCoord -.5*iResolution.xy ) / iResolution.x;
    uv = vec2(uv.y+uv.x, uv.y-uv.x) / sqrt(2.0);
    uv += vec2(iTime*0.05, 0);

    float s = 0.2;
    float w = 0.005;

    vec3 bg = vec3(0.0);
    vec3 fg = vec3(0.62, 0.51, 0.11);

    vec3 col = bg;


    if (mod(uv.y-uv.x, s) < w || mod(uv.y-uv.x, s) > s-w) {
        col = fg;
        }

    vec2 llc = vec2(uv.x - mod(uv.x, s), uv.y - mod(uv.y, s));

    float xd = uv.x-llc.x;
    float yd = uv.y-llc.y;

    if (xd-yd/3.0 < w && xd-yd/3.0 > -w) {
        col=fg;
        }
    if (yd-xd/3.0 < w && yd-xd/3.0 > -w) {
        col=fg;
        }

    if (xd-yd/1.5 < w && xd-yd/1.5 > -w) {
        col=fg;
        }
    if (yd-xd/1.5 < w && yd-xd/1.5 > -w) {
        col=fg;
        }

    if (mod(uv.x, s) < w || mod(uv.x, s) > s-w) {
        col = fg;
        }
    if (mod(uv.y, s) < w || mod(uv.y, s) > s-w) {
        col = fg;
        }


    // Output to screen
    fragColor = vec4(col,1.0);
}
