void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float dist = length(uv);

    vec3 coolColorx = sin(vec3(0.0, 2.0, 2.0));
    vec3 warmColorx = sin(vec3(0.0, 1.0, 2.0));

    vec3 temp1 = mix(coolColorx, warmColorx, uv.x +1.5);

    vec3 orange = vec3(1.0, 0.5, 0.0);
    vec3 yellow = vec3(1.0, 1.0, 0.0);

    vec3 temp2 = mix(orange, yellow, uv.y + 0.2);

    vec3 col = vec3(step(sin(dist * 15.0 + (5.0 * iTime)), 0.5));

    col *= mix(temp1, temp2, uv.x + 0.5);

    fragColor = vec4(col, 1.0);
}
