float map(float x, float a, float b, float c, float d){

    return c+(((x-a)*(c-d))/(a-b)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 8.0;
    uv.xy -= 4.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float numberlinex = uv.x+iTime;

    float xval = cos(numberlinex);
    float val1 = smoothstep( xval-0.02, xval, (0.2*cos(iTime)+0.9)*uv.y) -
          smoothstep( xval, xval+0.02, uv.y);

    float val2 = smoothstep( xval-0.02, xval, uv.y) -
          smoothstep( xval, xval+0.02, 0.9*uv.y);

    float m = map(uv.x, -4., 4., -1., 1.);
    float a = smoothstep( xval-(0.5+((2.*cos(iTime)+2.)*(abs(cos(numberlinex))))), xval, uv.y);
    float b = smoothstep( xval, xval+(0.5+((2.*cos(iTime)+2.)*(abs(cos(numberlinex))))), uv.y);
    float v = (1.-(a-b));
    float cv = pow(0.1/v, 1.);

    vec3 color = cv*vec3(.2, 0.0,.5*cos(numberlinex+10.*sin(iTime))+.5);
    // Output to screen
    fragColor = vec4(color, 1.0);
}
