#define PI 3.14159


vec2 chaos(vec2 uv)
{

    return vec2(cos(uv.x), sin(uv.y)) * 0.2;

}



float line(vec2 p, vec2 a, vec2 b)
{


    vec2 pa = p - a;
    vec2 ba = b - a;

    float t = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * t);

}


void mainImage( out vec4 fragColor, in vec2 u )
{

    vec2 r = iResolution.xy, uv = (u + u - r) / r.y; 


    uv.x += 3.6;
    uv.y +=- 1.1;
    uv *= 0.4;
    vec2 uvfr = fract(uv * 1.0) - 0.5;
    vec2 idr = floor(uv * 1.0);;

    vec3 color = vec3(0.0);

    // Set this line to 666.0
    float fs = length(uv) * 2.0;
    float f = 0.1 / line(uvfr, chaos(vec2(idr * fs + iTime)), chaos(vec2(idr * fs * 1.0 + iTime)));

    float fsg = length(uv) * 31.0;
    float fg = 0.1 / line(uvfr, chaos(vec2(idr * fsg + iTime)), chaos(vec2(idr * fsg * 1.0 + iTime)));

    float fsb = length(uv) * 545.0;
    float fb = 0.1 / line(uvfr, chaos(vec2(idr * fsb + iTime)), chaos(vec2(idr * fsb * 1.0 + iTime)));

    color.r = pow(f, 7.0);
    color.g = pow(fg, 7.0);
    color.b = pow(fb, 7.0);

    fragColor = vec4(color, 1.0);
}
