void SetCircle1(out vec2 c1, out float r1){
    r1 = 0.26;
    c1 = vec2(abs(sin(-iTime))*0.45+0.9, tan(-iTime)*0.99);
}

void SetCircle2(out vec2 c2, out float r2){
    r2 = 0.13;
    c2 = vec2(abs(sin(-iTime))*0.52+0.53,-tan(iTime)*0.8+0.46);
}

void SetCircle3(out vec2 c3, out float r3){
    r3 = 0.24;
    c3 = vec2(abs(sin(iTime-58.0))*0.5+0.8, cos(-iTime+5.0)*sin(iTime+0.99)+0.5);
}


void setBG(out vec3 BGColor){
    BGColor = vec3(0.6, 0.4 , 0.9);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    vec3 backgroundColor = cos(vec3(0.8,0.21,0.3));
    fragColor = vec4(backgroundColor, 1.0);
    vec2 tmp = fragCoord/iResolution.xy;
    vec2 c1, c2, c3;
    float r1, r2, r3;
    SetCircle1(c1, r1);
    SetCircle2(c2, r2);
    SetCircle3(c3, r3);
    tmp.x *= iResolution.x/iResolution.y;
    if ((tmp.x - c1.x)*(tmp.x - c1.x) + (tmp.y - c1.y)*(tmp.y - c1.y) <= r1*r1){
        fragColor = vec4(0.9, 0.7, 0.36, 0.0);
    }
    if ((tmp.x - c3.x)*(tmp.x - c3.x) + (tmp.y - c3.y)*(tmp.y - c3.y) <= r3*r3){
        fragColor = vec4(0.67, 0.6, 0.8, 0.0);
    }
    if(tmp.y <= 0.45 && tmp.x>=0.4)
        fragColor = vec4(0.3, 0.6, 0.1, 0.0);
    if ((tmp.x - c2.x)*(tmp.x - c2.x) + (tmp.y - c2.y)*(tmp.y - c2.y) <= r2*r2){
        fragColor = vec4(0.1, 0.7, 0.9, 0.0);
    }
}
