/*this is a renderer for a triangle it takes three cornerpoints (a ,b and c)
  and one point p, it gives back a vector with three values
  the first falue is 1 if p is inside the triangle and 0 if ouside.
  the last two values are the uv cordinates for the triangle wich you can
  use to display a texture on the triangle.
  */


vec3 t (vec2 p,vec2 a,vec2 b,vec2 c){
    float w1 = (a.x*(c.y-a.y)+(p.y-a.y)*(c.x-a.x)-p.x*(c.y-a.y))/
               ((b.y-a.y)*(c.x-a.x)-(b.x-a.x)*(c.y-a.y));

    float w2 = (p.y-a.y-w1*(b.y-a.y))/(c.y-a.y);

    float i  = 1.;
    if(min(w1,w2)<=0.)i=0.;
    if(w1+w2>=1.)i=0.;

    return vec3(i,w1,w2);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec2 a  = vec2(0.1,0.9);
    vec2 b  = vec2(0.1,0.1);
    vec2 c  = vec2(0.9,0.1);

    vec3 tc = t(uv,a,b,c);

    vec4 e  = texture(iChannel0,tc.yz,0.)*(tc.x);

    fragColor = e;
}
