#define pi 3.14159265
#define speed .4


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

uv.x*=cos(0.25*fragCoord.x)+sin(.33*fragCoord.y);
uv.y*=sin(.25*fragCoord.y)-cos(.33*fragCoord.x);

if(iMouse.z>0.){
uv.x*=cos(.01*iMouse.x)+sin(.015*iMouse.y);
uv.y*=sin(.01*iMouse.y)-cos(.015*iMouse.x);
}


    vec3 col = vec3(.0);

    col.r=uv.x*sin(iTime*pi*speed)*texture(iChannel0,uv).x;
    col.g=uv.x*cos(iTime*pi*speed/2.)*texture(iChannel0,uv).y+uv.y*sin(iTime*pi*speed/2.)*texture(iChannel0,uv).x;
    col.b=uv.x*sin(iTime*pi*speed/1.5)*texture(iChannel0,uv).y;

    // Output to screen
    fragColor = vec4(col,1.0);
}
