float dist(vec2 v){
   return v.x*v.x+v.y*v.y;
}


const float TPI = atan(1.0)*8.0;


vec2 f(vec2 v){
  v-=0.5;

  float vv= TPI*fract(0.052*iTime)*floor(1.0+3.30*sin(0.4*iTime+5.0*dist(v)));

  float xx =  v.x*cos(vv)+v.y*sin(vv);//fract(fract(v.x*2.0)+rep(fract(0.52*(iTime)+v.x)));

  float yy = v.y*cos(vv)-v.x*sin(vv);//ract(fract(v.y*3.0)+iTime+9.0*rep(fract(0.83*(iTime)+v.y)));


  return vec2(xx,yy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p =  1.0*fragCoord/iResolution.x;
    fragColor = vec4(texture(iChannel0,f(p)));
}
