vec2 distort(vec2 uv, float time) {
    float repeat_x = 4.0;
    float repeat_y = 4.0;
    float speed = 2.0;
    float power = 0.01;
	return uv + vec2(
      sin((sin(uv.x * repeat_x * 3.14) + (uv.y * repeat_y) + time * speed) * 3.14) * power, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = distort(uv, iTime);
	fragColor = vec4(texture(iChannel0, uv) );
}
