/*    
      just about any function f:  R^2-->R^2 
      where f(v) = v + e*g(v)
      with div.g =0 
      and e small cte,
      will give the impression of
      fluid-like movement
 */

vec2 f(vec2 v){
      float a = cos(3.012*iTime);
      float b = sin(2.1*iTime);
      return v+0.03*vec2(
          cos(9.0*v.y*a)*sin(8.0*v.y),
          sin(8.0*v.x)*sin(7.0*v.x*b)

      );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;


    // Output to screen
    fragColor = texture(iChannel0,f(uv));
}
