// Function to generate a random number based on tile position
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 truchet(vec2 uv, vec3 col, vec3 colIn)
{
    vec2 tile = floor(uv); // Coordenadas de la baldosa
    vec2 pos = fract(uv);  // Coordenadas locales dentro de la baldosa

    // Alternancia basada en si la posici√≥n es par o impar
    if (mod(tile.x + tile.y, 2.0) < 1.0) {
        col = fig01(pos, col); // blanca
    } else {
        col = fig02(pos, col); // negra
    }

    return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float scale =2.0;
   vec2 uv =  scale * ( 2. * fragCoord - iResolution.xy ) / iResolution.y ;
   vec3 col=vec3(1.0);
   col= truchet(uv,col,vec3(1.0));
   col=pow(col,  vec3(0.554545));
   fragColor = vec4(col,1.0);
}
