vec2 Pol(vec2 p){
    float a = atan(p.x, p.y);
    float d = length(p);
    return vec2(a, d);
}

float N21(vec2 p){
    return fract(dot(p.x*46.23, p.y*47.42)*47.411+93.23);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 10.;
    float c = cos(iTime), s = sin(iTime);
    //uv *= mat2(c, -s, s, c);
    uv = abs(uv);
    vec2 gv = fract(uv)-.5;
    gv*= mat2(c, -s, s, c);
    float r = .2; // change for different effects.
    gv += r/length(uv);

     //fun effect
    float d = length(uv-uv.x)*sin(uv.x+uv.y);
    uv *= mat2(cos(d), -sin(d*2.), sin(d), cos(d*2.));

    vec2 id = floor(uv);
    vec3 col = vec3(0);
    vec2 pv = Pol(uv/3.);


    float t = iTime*.8;
    vec3 base = sin(t*vec3(1.276, 2.891, 4.461)+Pol(id).y)*.4+.8; 
    float fade = mix(.0, -2., cos(Pol(id).y+t)*2.);
    float size = 1.;// N21(id+.1);
    float wheel = fract(pv.x+t) - floor(pv.y*size)*.5;
    col.r += sin(pv.x+iTime);
    col += wheel*base*fade;
    col -= .2;

    fragColor = vec4(col,1.0);
}
