/* sedcore - eclipse - 2024-04 - public domain
 * rewrite of an effect (by me) found at http://sedcore.eu.org/small/ (dig for "eclipse" in there)
 *
 * Not exactly the same effect. In the original, computation is sequential.
 * Seems impossible to do with the shader paradigm.
 */

/* hash11 from https://www.shadertoy.com/view/4djSRW */
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

/* ign from https://www.shadertoy.com/view/XlGcRh */
float ign(vec2 v)
{
    vec3 magic = vec3(0.06711056, 0.00583715, 52.9829189);
    return fract(magic.z * fract(dot(v, magic.xy)));
}

#define CIRCLE (0.225/2.)
#define DELTA_POINT 0.05
#define MOON .5
#define SUN (.5+.1)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (2.*fragCoord-iResolution.xy) / iResolution.y;
  /* loop of 8s */
  float t = mod(iTime,8.);
  /* noise moves at 25 fps */
  vec2 noise = vec2((ign(vec2(floor(t*25.),0.))-.5)/10.,(ign(vec2(floor(t*25.),1.))-.5)/10.);
  uv+=noise;
  vec2 moon_pos = vec2(-1.+t/8.*2.);
  vec2 min = DELTA_POINT * floor((uv - CIRCLE - .05) / DELTA_POINT);
  vec2 max = DELTA_POINT * floor((uv + CIRCLE + .05) / DELTA_POINT);
  float col = 0.;
  for (float x = min.x; x <= max.x; x += DELTA_POINT)
  for (float y = min.y; y <= max.y; y += DELTA_POINT) {
    vec2 cur = vec2(x,y);
    if (length(cur) > SUN) continue;
    float intensity = clamp(length(cur-moon_pos)-MOON,0.,1.);
    float l = length(uv-cur);
    /* here also noise moves at 25 fps */
    if (l < CIRCLE)
      if (hash11(x+10. + (y+10.) * 100. + floor(t*25.) * 1000.) > 0.2)
        col += clamp(1.-l/CIRCLE,0.,1.) * intensity;
  }
  fragColor = vec4(col,col,col,1.0);
}
