void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float start_pos = 0.0;
    float max_dist = 2.0;
    vec2 pos = vec2(0.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspect_ratio = iResolution.x / iResolution.y;

    vec2 rect_size = vec2(0.5f, 0.5f);

    vec2 cam = vec2(iTime * 0.4, 0.0) + (uv - 0.5f);

    float offs = floor((cam.x - (start_pos - max_dist * 0.5)) / max_dist) * max_dist;

    vec2 rect_start = vec2(pos.x + offs, pos.y) - rect_size * 0.5;
    vec2 rect_end = vec2(pos.x + offs, pos.y) + rect_size * 0.5;

    vec2 screen_pos = cam + (uv - 0.5);

    // Time varying pixel color
    vec3 col = vec3(0.2, 0.5, 1);

    if (clamp(screen_pos, rect_start, rect_end) == screen_pos) {
        // inside of rect

        vec2 rect_uv = (screen_pos - rect_start) / (rect_end - rect_start);
        vec4 tex = texture(iChannel0, rect_uv);

        float dist = (1.0 - ((length(rect_uv - 0.5)) / 0.5)) * 2.0 - 1.0;
        float cloud_sdf = clamp(dist + tex.x, 0.0, 1.0);

        col = mix(col, vec3(1.0), cloud_sdf);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
