float moonRadius = 0.12;
vec2 sunPosition = vec2(0,0.1);
float sunExp = 25.0;
vec3 sunTint = vec3(1.0,0.95,0.9);
vec3 skyColorTop = vec3(0.1,0.2,0.3);
vec3 skyColorBottom = vec3(1.0,0.6,0.2);
float skyGlowExp = 6.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 skyUV = fragCoord / iResolution.xy;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy ) / iResolution.y;
    uv-=sunPosition;
    //Sunset glow
    vec3 color = mix(skyColorTop,skyColorBottom,pow(1.0-skyUV.y,skyGlowExp));

    //Subtle cloud texture
    color*=1.0-texture(iChannel0,vec2(skyUV.x*0.5,0.01/skyUV.y-iTime*0.02)).r*pow(1.0-skyUV.y,6.0)*0.2;

    //Circle SDF
    float moonD = length(uv) - moonRadius;

    //Sun glow falloff
    float sunD = pow(1.0-moonD,sunExp);
    vec3 sunColor = vec3(sunD)*sunTint;

    //Scrolling noise
    sunColor*=texture(iChannel0,(skyUV+vec2(0,iTime*0.1))*0.35).r*0.5+0.5;
    color+=sunColor;

    //Draw black circle for moon
    color = mix(vec3(0.0),color,smoothstep(-0.003,0.003,moonD));

    //Mountains
    float y = abs(fract(skyUV.x*5.0)-0.5)*0.1;
    y+=abs(fract(skyUV.x*12.0)-0.5)*0.05;
    y+=sin(skyUV.x*8.0)*0.05;
    y+=sin(skyUV.x*15.0)*0.02;
    y+=0.01;
    float m1 = smoothstep(y-0.003,y+0.003,skyUV.y);
    color = mix(vec3(0.1,0.1,0.1),color,m1);

    // Output to screen
    fragColor = vec4(color,1.0);    


}
