void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float colorSpace = uv.x;
    if ((mod(fragCoord.x,16.0)<8.0)^^(mod(fragCoord.y,16.0)<8.0)) {
        colorSpace = mod(fragCoord.x+fragCoord.y,2.0);
    } else {
        if (iMouse.z > 0.0) {
            colorSpace = pow(colorSpace, 1.0);
        } else {
            colorSpace = pow(colorSpace, 1.0/2.2);
        }
    }

    // Output to screen
    fragColor = vec4(vec3(colorSpace),1.0);
}
