// 0 to Y Axis,
// 1 to X Axis.
#define AXIS 0
float waveAMP = 20.0;
bool flip = false;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float col = 1.0;
    #if AXIS == 0
    if (flip)
        col = tan(iTime-(uv.y+cos((uv.x+iTime*0.5)*waveAMP+iTime)*0.075)) * 2.0;
    else
        col = tan(iTime+(uv.y+cos((uv.x+iTime*0.5)*waveAMP+iTime)*0.075)) * 2.0;
    #elif AXIS == 1
    if (flip)
        col = tan(iTime-(uv.x+cos((uv.y+iTime*0.5)*waveAMP/2.0+iTime)*0.075)) * 2.0;
    else
        col = tan(iTime+(uv.x+cos((uv.y+iTime*0.5)*waveAMP/2.0+iTime)*0.075)) * 2.0;
    #endif
    vec4 tex = texture(iChannel0, uv);
    tex.rgb /= col;
    fragColor = tex;
}






