    Copyright ( c ) 2024 KKlabs.

*/

#define h(a) fract(sin(dot(a, vec2(12.345, 54.321))) * 9876.5432)
#define I iResolution
#define A min(I.x, I.y)

float th(vec2 u) {

  vec2 v = u, t;

  u += u.x + u.y > 0. ? -.2 : .2;

  t = clamp(round(v / .2), -1., 1.);
  v -= .2 * t;

  return min(t.x + t.y == 0. ? 1. : length(v) - .1, length(u) - .2);
}

float te(vec2 u) {
  vec2 v = u, t, b = round(u / .4);

  u -= .4 * round(u / .4);

  if (h(round(b + sin(iTime))) < .3) {
    u.x *= -1.;
  }

  if (h(b) > .8) {
    u.y *= -1.;
  }

  u += .2;

  t = clamp(round(u / vec2(.4, .4)), 0., 1.);

  u -= vec2(.4, .4) * t;

  /*
      // square inversed
      if( h(t) * h(b)  > .6 )
      u.x *= -1.;
  */

  return smoothstep(2. / A, -2. / A, abs(th(u) + .05) - .02);
}

float signRound(vec2 u, float s) {

  vec2 t = round(u / s / 4.);

  u -= s * 4. * t;

  if (h(round(t + sin(sin(iTime * 5.) + h(t) * iTime))) < .5)
    return 1.;

  return mod(t.x + t.y, 2.) < 1. ? 1. : length(u) - s * .25;
}

void mainImage(out vec4 U, in vec2 V) {

  vec2 u = (V * 2. - I.xy) / A - iTime * .1;

  vec3 c;

  c = mix(c + .5, vec3(.4), te(u + .02));

  c = mix(c, vec3(1), te(u));

  c = mix(c, vec3(1.), smoothstep(2. / A, -2. / A, signRound(u, .05)));

  U = vec4(c, 1.0);
}
