/*
    Roses are red and violets are blue,
    I write this boilerplate every single time.
*/

const float gZoom       = 0.8;
      float gT;         // set in main
      float gLineWidth; // set in main
      float gAAEps;     // set in main

// from "Smooth HSV" by Inigo Quilez
// https://www.shadertoy.com/view/MsS3Wc
// Official HSV to RGB conversion 
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return c.z * mix( vec3(1.0), rgb, c.y);
}
// Smooth HSV to RGB conversion 
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage(out vec4 RGBA, in vec2 XY) {
    gT          = iTime;
    gLineWidth  = VIEWFROMSCREEN_DIST(4.0);
    gAAEps      = VIEWFROMSCREEN_DIST(2.0);
    vec2 p      = VIEWFROMSCREEN     (XY );

    float reps  = 7.0;
    float theta = atan(p.y, p.x);
    float h     = reps * (theta / pi / 2.0);

    vec3 rgb = hsv2rgb_smooth(vec3(h, 1.0, 1.0));

    RGBA.rgb  = rgb;

    // housekeeping
    RGBA.rgb *= 1.0 - pow(LENGTHSQR(XY/RES - 0.5) * 1.5, 3.0);
    RGBA.rgb  = pow(RGBA.rgb, vec3(1.0 / 2.2));
    RGBA.a    = 1.0;
}

const vec2  v01 = vec2(0.0, 1.0);
const vec3  vX  = v01.yxx;
const vec3  vY  = v01.xyx;
const vec3  vZ  = v01.xxy;
const vec3  v0  = v01.xxx;
const vec3  v1  = v01.yyy;
const float pi  = 3.14159265359;

#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

#define VIEWFROMSCREEN_DIST(distance)                     \
        ( (distance) / MINRES / gZoom                     )
#define VIEWFROMSCREEN(xy)                                \
        ( vec2(VIEWFROMSCREEN_DIST((xy).x * 2.0 - RES.x), \
               VIEWFROMSCREEN_DIST((xy).y * 2.0 - RES.y)) )

#define LENGTHSQR(a) ( dot((a), (a)) )
