float f(float x) {
    // return value of function here
    return sin(x);
}

float impl_f(float x, float y, float eq) {
    // use this for an implicit function like a circle
    return x*x + y*y - eq;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;

    float epsilon = 1.;
    float scale = 10.;

    float xc = scale * (uv.x - 0.5);
    float yc = scale * (uv.y - 0.5);

    float axWidth = 0.01;

    float v = abs(f(xc) - yc);
    //float v = abs(impl_f(xc, yc, 0.5));

    float thresh = epsilon / scale;
    if (abs(xc) < axWidth || abs(yc) < axWidth) {
        fragColor = vec4(1,1,1,1);
    } else if (v < thresh) {
        fragColor = vec4(1,1,0,0);
    } else {
        fragColor = vec4(0,0,0,0);
    }
}
